import { useEffect, useState } from "react";
import { ItemList } from "../components/ItemList/ItemList.js";
import { db} from "../firebase/firebase";
import 'firebase/firestore'
import Spinner from "../components/Spinner/Spinner";
import "./Home.scss";
import {useParams} from 'react-router-dom'


const Home = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  const {Id} = useParams();

  console.log("CATEGORIA",Id);

  useEffect(() => {
//1-armar la referencia
   // const productref= collection(db,'productos')

//2get a esa referncias product
/* getDocs(productref)
.then((collection)=>{
const item=collection.docs.map((doc)=>{
  id:doc.id,
  ...doc.data()
})
setItems(item)
})
.finally(()=>{
  setLoading(false)
}) */

    const itemsCollection = collection("productos");
    var query = Id ? itemsCollection.where("categoria", "==", Id): itemsCollection;

    const prom = query.get();
    prom.then((resultado) => {

      if (resultado.size > 0) {

        setItems(resultado.docs.map((doc) => ({
          id: doc.id,
          ...doc.data()})));
        setTimeout(() => {
          setLoading(false);
        }, 1000);

      }
    });
  }, [Id]);
  console.log("products en Home", items);

  return (
    <>
      {loading ? (
        <div id="Home" className="home">
          <Spinner />
        </div>
      ) : (
        <div className="container">
          <>
            <ItemList items={items} />
          </>
        </div>
      )}
    </>
  );
};
export default Home;
