import * as React from "react";
import { useState, Fragment } from "react";
import { useContext } from "react";
import Box from "@mui/material/Box";
import Swal from 'sweetalert2'
import "./login.scss";
import "firebase/firestore";
import { useNavigate } from "react-router-dom";
import { Grid } from "@material-ui/core";
import CardActions from "@mui/material/CardActions";

import Card from "@mui/material/Card";



    import {addDoc, collection,documentId,getDocs, where,  
      query,getFirestore,writeBatch} from 'firebase/firestore'
      import{db} from '../../firebase/firebase.js'


import { CartCntext2 } from "../../context/CartCntext2";

function RedBar() {
  return (
    <Box
      sx={{
        height: 20,
        backgroundColor: (theme) =>
          theme.palette.mode === "light"
            ? "rgba(255, 0, 0, 0.1)"
            : "rgb(255 132 132 / 25%)",
      }}
    />
  );
}
const confirm = () => {
  Swal({
    position: "top-end",
    icon: "success",
    title: "Purchase Confirmed",
    showConfirmButton: false,
    timer: 1500,
  });
};
export default function Login() {
  let navigate = useNavigate();
  const { cart, clear, totalPrice } = useContext(CartCntext2);
  const [user, setUser] = useState({
    name: "",
    surname: "",
 phone:""

  });

  const crearOrden = async () => {
    const db = getFirestore();

    const newOrder = {
      buyer: user,
      item: [{ ...cart }],
      total: totalPrice(),
    };

  ;
  const batch = writeBatch(db);

    const orderRef = collection(db, "orders");
    const productosRef = collection(db, "productos");
    const q = query(
      productosRef,
      where(
        documentId(),
        "in",
        cart.map((el) => el.id)
      )
    );

    const outOfStock = [];

    const productos = await getDocs(q);

    productos.docs.forEach((doc) => {
      const itemToUpdate = cart.find((prod) => prod.id === doc.id);
      if (doc.data().stock >= itemToUpdate.quantity) {
        batch.update(doc.ref, {
          stock: doc.data().stock - itemToUpdate.quantity,
        });
      } else {
        outOfStock.push(itemToUpdate);
      }
    });

    if (outOfStock.length === 0) {
      addDoc(orderRef, newOrder).then((res) => {
        batch.commit();

        Swal.fire({
          icon: "succes",
          title: "su orden se creo con exito",
          text: `Su numero de oden es: ${res.id}`,
        });
        clear();
      });
    } else {
      Swal.fire({
        icon: "error",
        title: "No hay stock de los siguientes productos:",
        text: outOfStock.map((el) => el.name).join(","),
      });
    }
  };
  /*     ordersCollection
    .add(newOrder)
    .then((docRef)=>console.log("se creo el documento exitisamente",docRef.id))
    .catch((error)=> console.log(error));
    confirm();
    clear(); */
  // }}

  const capturoDatos = (event) => {
    console.log(event.target.value);
    setUser({
      ...user,
      [event.target.name]: event.target.value,
    });
  };

  const sendDatos = (event) => {
    event.preventDefault();
    console.log("USER", user);
  };

  return (
    <Grid container justifyContent="center">
          <Grid item xs={10}>
            <Container maxWidth="md">
              <div className={classes.formWrapper}>
                <Formik
                  initialValues={{
                    ...(INITIAL_FORM_STATE || ""),
                  }}
                  validationSchema={FORM_VALIDATION}
                  onSubmit={(values) => onSubmit(values)}
                >
                  <Form>
                    <Grid container spacing={1}>
                      <Grid item xs={12}>
                        <Textfield
                          name="dni"
                          label="Dni"
                          placeholder="99999999"
                        />
                      </Grid>

                      <Grid item xs={12}>
                        <Textfield
                          name="cuil"
                          label="Cuil"
                          placeholder="ingresar cuil sin guiones"
                        />
                      </Grid>

                      <Grid item xs={12}>
                        <Textfield
                          name="lastName"
                          label="Apellido"
                          placeholder="Apellido"
                        />
                      </Grid>

                      <Grid item xs={12}>
                        <Textfield
                          name="firstName"
                          label="Nombre"
                          placeholder="Nombre"
                        />
                      </Grid>

                      <Grid item xs={12}>
                        <DateTimePicker
                          name="nacimiento"
                          label="FechaNacimiento"
                        />
                      </Grid>

                      <Grid item xs={12}>
                        <Textfield
                          name="email"
                          label="Email"
                          placeholder="eeeeee@dominio.com"
                        />
                      </Grid>

                     {/*  <Grid item xs={12}>
                        <Textfield
                          name="email2"
                          label="Email"
                          placeholder="repetir email"
                        />
                      </Grid> */}

                      <Grid item xs={12}>
                        <Select
                          name="situacion"
                          label="Situacion de Revista"
                          options={situacion}
                        />
                      </Grid>

                      <Grid item xs={12}>
                        <Select name="nivel" label="Nivel" options={nivel} />
                      </Grid>


                      <div style={{ marginLeft: "auto", marginRight: "auto" }}>
                      <br/>
                        <ReCAPTCHA
                          ref={captcha}
                          sitekey="6LfCt5keAAAAAOEAoTbu3JpTmAhDk1Wrblv8Y-EA"
                          onChange={onChange}
                        />
                           <br/>
                      </div>

                      <Grid item xs={12}>
                        <Button>Submit Form</Button>
                      </Grid>
                    </Grid>
                  </Form>
                </Formik>
              </div>
            </Container>
          </Grid>
        </Grid>
  )
}
