import * as React from "react";
import { useState, Fragment } from "react";
import { useContext } from "react";
import Box from "@mui/material/Box";
import swal from "sweetalert";
import "./login.scss";
import "firebase/firestore";


  import { getFirestore,    
    query} from "../../firebase/firebase";
    import {addDoc, collection,documentId} from 'firebase/firestore'


import { CartCntext2 } from "../../context/CartCntext2";

function RedBar() {
  return (
    <Box
      sx={{
        height: 20,
        backgroundColor: (theme) =>
          theme.palette.mode === "light"
            ? "rgba(255, 0, 0, 0.1)"
            : "rgb(255 132 132 / 25%)",
      }}
    />
  );
}
const confirm = () => {
  swal({
    position: "top-end",
    icon: "success",
    title: "Purchase Confirmed",
    showConfirmButton: false,
    timer: 1500,
  });
};
export default function Login() {
  const { cart, clear, totalPrice } = useContext(CartCntext2);
  const [user, setUser] = useState({
    name: "",
    surname: "",
    email: "",
    emailConfirm: "",
    date: getFirestore.Timestamp,
  });
  const db = getFirestore();
  const crearOrden = async () => {

    const newOrder = {
      buyer: user,
      item: [{ ...cart }],
      total: totalPrice(),
    };

  ;

    const batch = db.batch();

    const orderRef = collection(db, "orders");
    const productosRef = collection(db, "productos");
    const q = query(
      productosRef,
      where(
        documentId(),
        "in",
        cart.map((el) => el.id)
      )
    );

    const outOfStock = [];

    const productos = await getDocs(q);

    productos.docs.forEach((doc) => {
      const itemToUpdate = cart.find((prod) => prod.id === doc.id);
      if (doc.data().stock >= itemToUpdate.quantity) {
        batch.update(doc.ref, {
          stock: doc.data().stock - itemToUpdate.quantity,
        });
      } else {
        outOfStock.push(itemToUpdate);
      }
    });

    if (outOfStock.length === 0) {
      addDoc(orderRef, newOrder).then((res) => {
        batch.commit();

        swal.fire({
          icon: "succes",
          title: "su orden se creo con exito",
          text: `Su numero de oden es: ${res.id}`,
        });
        clear();
      });
    } else {
      swal.fire({
        icon: "error",
        title: "No hay stock de los siguientes productos:",
        text: outOfStock.map((el) => el.name).join(","),
      });
    }
  };
  /*     ordersCollection
    .add(newOrder)
    .then((docRef)=>console.log("se creo el documento exitisamente",docRef.id))
    .catch((error)=> console.log(error));
    confirm();
    clear(); */
  // }}

  const capturoDatos = (event) => {
    console.log(event.target.value);
    setUser({
      ...user,
      [event.target.name]: event.target.value,
    });
  };

  const sendDatos = (event) => {
    event.preventDefault();
    console.log("USER", user);
  };

  return (
    <div className="formu">
      <Fragment>
        <form className="culumn" onSubmit={sendDatos}>
          <div className="col-md-12 ">
            <input
              placeholder="Nombre"
              className="form-control"
              type="text"
              name="name"
              onChange={capturoDatos}
            ></input>
            <br />
          </div>
          <div className="col-md-12">
            <input
              placeholder="Apellido"
              className="form-control"
              type="text"
              name="surname"
              onChange={capturoDatos}
            ></input>
          </div>
          <br />
          <div className="col-md-12">
            <input
              placeholder="Email"
              className="form-control"
              type="text"
              name="email"
              onChange={capturoDatos}
            ></input>
          </div>
          <br />
          <div className="col-md-12">
            <input
              placeholder="Email"
              className="form-control"
              type="text"
              name="emailConfirm"
              onChange={capturoDatos}
            ></input>
          </div>
          <br />
          <div className="boton">
            <button
              className="btn btn-primary"
              type="submit"
              onClick={crearOrden}
            >
              Enviar
            </button>
          </div>
        </form>
      </Fragment>
    </div>
  );
}
