import * as React from "react";
import { useState, Fragment } from "react";
import { useContext } from "react";
import Box from "@mui/material/Box";
import swal from "sweetalert";
import "./login.scss";
import { getFirestore } from "../../firebase/firebase";
import { CartCntext2 } from "../../context/CartCntext2";

function RedBar() {
  return (
    <Box
      sx={{
        height: 20,
        backgroundColor: (theme) =>
          theme.palette.mode === "light"
            ? "rgba(255, 0, 0, 0.1)"
            : "rgb(255 132 132 / 25%)",
      }}
    />
  );
}
const confirm = () => {
  swal({
    position: "top-end",
    icon: "success",
    title: "Purchase Confirmed",
    showConfirmButton: false,
    timer: 1500,
  });
};
export default function Login() {


  const { cart, clear, totalPrice } = useContext(CartCntext2);
  const [user, setUser] = useState({
    name: "",
    surname: "",
    email: "",
  });

  const newOrder={
    buyer:user,
    item:[{...cart}],
    total: totalPrice(),
  }
  const crearOrden=() =>{
    const db = getFirestore();
    const ordersCollection= db.collection('orders');

    ordersCollection
    .add(newOrder)
    .then((docRef)=>console.log("se creo el documento exitisamente",docRef.id))
    .catch((error)=> console.log(error));
    confirm();
    clear();
  }

  const capturoDatos = (event) => {
    console.log(event.target.value);
    setUser({
      ...user,
      [event.target.name]: event.target.value,
    });
  };


  const sendDatos = (event) => {
    event.preventDefault();
    console.log("USER", user);
  };

  return (
    <div className="formu">
      <Fragment>

      <form className="culumn" onSubmit={sendDatos}>
        <div className="col-md-12 ">
          <input
            placeholder="Nombre"
            className="form-control"
            type="text"
            name="name"
            onChange={capturoDatos}
          ></input>
            <br/>
        </div>
        <div className="col-md-12">
          <input
            placeholder="Apellido"
            className="form-control"
            type="text"
            name="surname"
            onChange={capturoDatos}
          ></input>
        </div >
        <br/>
        <div className="col-md-12">
          <input
            placeholder="Email"
            className="form-control"
            type="text"
            name="email"
            onChange={capturoDatos}
          ></input>
           </div >
            <br/>
          <div className="boton">
            <button
              className="btn btn-primary"
              type="submit"
              onClick={crearOrden}
            >
              Enviar
            </button>
          </div>

      </form>
      </Fragment>
    </div>
  );
}
