}) */
const db = getFirestore();



    const itemsCollection = collection(db,"productos");
    var query = Id ? itemsCollection.where("categoria", "==", Id): itemsCollection;


    if (Id) {
      const itemQuery = query(itemCollection, where('category', '==', Id));
      getDocs(itemQuery).then((snapshot) => {
          setProducts(snapshot.docs.map((doc) => ({
              ...doc.data(),
              id: doc.id
          })))
          setLoading(false)
      })
  } else {
      getDocs(itemCollection).then((snapshot) => {
          setProducts(snapshot.docs.map((doc) => ({
              ...doc.data(),
              id: doc.id
          })))
          setLoading(false)
      })
    }
  }, [Id]);
  console.log("products en Home", items);


  useEffect(() => {

    const db = getFirestore();

    const itemCollection = collection(db, 'items');

    if (Id) {
        const itemQuery = query(itemCollection, where('category', '==', Id));
        getDocs(itemQuery).then((snapshot) => {
            setProducts(snapshot.docs.map((doc) => ({
                ...doc.data(),
                id: doc.id
            })))
            setLoading(false)
        })
    } else {
        getDocs(itemCollection).then((snapshot) => {
            setProducts(snapshot.docs.map((doc) => ({
                ...doc.data(),
                id: doc.id
            })))
            setLoading(false)
        })
    }
}, [Id])

  return (
    <>
      {loading ? (
        <div id="Home" className="home">
          <Spinner />
        </div>
      ) : (
        <div className="container">
          <>
            <ItemList items={items} />
          </>
        </div>
      )}
    </>
  );
};
export default Home;
